INSERT INTO `news_vi` (`id`, `title_vi`, `summary_vi`, `content_vi`) VALUES
('dich-vu-1-3ddd0', 'Dịch Vụ 1', 'Tóm tắt', '<p>\r\n	Nội Dung</p>\r\n'),
('khi-co-thac-mac-ve-ve-dich-vu-toi-lien-he-voi-ai-nhu-the-n', ' Khi có thắc mắc về vé,dịch vụ tôi liên hệ với ai, như thế nào ?', '', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">Bạn c&oacute; thể li&ecirc;n hệ với ch&uacute;ng t&ocirc;i qua SDT hotline hoặc điền v&agrave;o form Tư Vấn(xem ở đ&acirc;y ) .Ngay sau đ&oacute; &iacute;t ph&uacute;t ch&uacute;ng t&ocirc;i sẽ li&ecirc;n hệ để giải đ&aacute;p những thắc mắc của bạn.</span><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">-&nbsp;</span><b style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; ">T&ocirc;i n&ecirc;n đặt v&eacute; v&agrave;o thời điểm n&agrave;o để c&oacute; gi&aacute; rẻ nhất ?</b><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">Bạn n&ecirc;n đặt v&eacute; c&agrave;ng sớm c&agrave;ng tốt để lựa chọn gi&aacute; v&eacute; gi&aacute; rẻ.</span><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">-&nbsp;</span><b style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; ">T&ocirc;i muốn biết Abay c&oacute; dịch vụ đặt chỗ trực tuyến kh&ocirc;ng(web check-in)</b><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">Bạn ho&agrave;n to&agrave;n c&oacute; thể đặt ghế ngồi tr&ecirc;n chuyến bay trực tuyến v&agrave; kh&ocirc;ng phải đến s&acirc;n bay sớm để xếp chỗ.</span><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">-&nbsp;</span><b style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; ">Ngo&agrave;i ph&iacute; mua v&eacute; tr&ecirc;n Abay t&ocirc;i c&oacute; phải đ&oacute;ng th&ecirc;m khoản ph&iacute; n&agrave;o kh&ocirc;ng ?</b><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">Bạn phải đ&oacute;ng th&ecirc;m ph&iacute; dịch vụ của h&atilde;ng h&agrave;ng kh&ocirc;ng tại s&acirc;n bay khi l&agrave;m thủ tục v&agrave; kh&ocirc;ng phải nộp khoản ph&iacute; n&agrave;o kh&aacute;c cho Abay.</span><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">-&nbsp;</span><b style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; ">L&agrave;m thế n&agrave;o t&ocirc;i nhận được v&eacute; ?</b><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">V&eacute; m&aacute;y bay điện tử được ban h&agrave;nh v&agrave; được gọi l&agrave; v&eacute; điện tử. Ngay sau khi bạn ho&agrave;n th&agrave;nh việc thanh to&aacute;n v&eacute; m&aacute;y bay của bạn, ch&uacute;ng t&ocirc;i sẽ gửi cho bạn v&eacute; điện tử của bạn v&agrave; c&aacute;c chi tiết đặt v&eacute; của bạn qua Email. Bạn kh&ocirc;ng cần phải c&oacute; một bản in v&eacute; điện tử của bạn khi đi đến s&acirc;n bay. Khi h&atilde;ng h&agrave;ng kh&ocirc;ng check-in, tất cả những g&igrave; bạn cần l&agrave;m l&agrave; xuất tr&igrave;nh hộ chiếu của bạn hoặc chứng minh nh&acirc;n d&acirc;n (nếu n&oacute; c&oacute; thể được sử dụng cho chuyến bay của bạn).</span><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">-&nbsp;</span><b style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; ">Điều g&igrave; xảy ra nếu t&ocirc;i bị mất v&eacute; ?</b><br style="font-family: arial, tahoma; color: rgb(51, 51, 51); text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: arial, tahoma; text-align: justify; ">V&eacute; điện tử của bạn được lưu trữ dưới dạng một d&atilde;y số bạn c&oacute; thể nhớ hoặc lưu ra bất cứ đ&acirc;u để khai b&aacute;o khi l&agrave;m thủ tục đặt chỗ. Nếu bạn đặt sai Email khi đăng k&iacute; mua v&eacute; hoặc qu&ecirc;n kh&ocirc;ng lưu số v&eacute; th&igrave; c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ lấy giống th&ocirc;ng tin trong hộ chiếu(chứng minh thư) của bạn. Bạn cũng c&oacute; thể li&ecirc;n hệ để ch&uacute;ng t&ocirc;i gửi lại cho bạn.</span></p>\r\n'),
('tuyen-nhan-vien-kinh-doanh-743cd', 'Tuyển nhân viên kinh doanh', 'Khu vực Hà Nội', '<p>\r\n	Đang Tuyển</p>\r\n'),
('xich-lo-net-van-hoa-h', 'Xích lô - nét văn hóa Hà Nội', 'Ban đầu, xe kéo được các nhân viên Pháp và quan lớn của Hà Nội mua và sử dụng. Càng về sau, nó trở thành biểu tượng cho sự giàu sang của các thương gia Hà thành. Mỗi người sắm xe kéo đều có một người chuyên kéo xe gọi là “culi” và đôi khi còn thêm 2 người khác đẩy. Nếu là quan lớn thì thường có người trẻ đi bên cạnh người phu kéo, tay cầm ống thuốc lào hoặc một khay trầu nếu người ngồi trên xe là phụ nữ. Theo thời gian, chiếc xe kéo được cải tiến về cấu tạo như: bánh xe được bọc vỏ cao su, đặt lò xo giữa bánh xe và ghế ngồi, trang trí thêm cho sang trọng ...', '<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Nhiều năm sau Chiến tranh thế giới lần thứ nhất, lần đầu ti&ecirc;n xuất hiện trong trung t&acirc;m th&agrave;nh phố xe đạp được nhập từ Saint Etienne, Ph&aacute;p. V&agrave; chiếc xe đạp đ&atilde; được gắn v&agrave;o ph&iacute;a sau của chiếc xe k&eacute;o, thế l&agrave; chiếc xe x&iacute;ch l&ocirc; đ&atilde; ra đời. Xe x&iacute;ch l&ocirc; đ&atilde; nối nghiệp xe k&eacute;o, đ&oacute;ng vai tr&ograve; quan trọng trong cải tiến phương tiện đi lại tr&ecirc;n c&aacute;c phố phường H&agrave; Nội. Chẳng bao l&acirc;u, x&iacute;ch l&ocirc; đựơc chuyển th&agrave;nh phương tiện chủ yếu l&agrave; để chở h&agrave;ng. Do vậy, một mặt n&oacute; được thiết kế vững ch&atilde;i hơn, bớt tiện nghi hơn, c&aacute;i đệm được thay bằng tấm gỗ th&ocirc; sơ nhưng chịu tải tốt, c&aacute;i tay ngai b&egrave; ra để c&oacute; thể chở được vật cồng kềnh, bớt đi mui che nắng v&agrave; tấm ni l&ocirc;ng chắn mưa, lại th&ecirc;m hai c&aacute;i m&oacute;c gắn v&agrave;o chỗ để ch&acirc;n của kh&aacute;ch để khi cần c&oacute; thể k&eacute;o xe vượt qua những địa h&igrave;nh kh&oacute;...</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong 2 cuộc kh&aacute;ng chiến của d&acirc;n tộc, x&iacute;ch l&ocirc; trở th&agrave;nh phương tiện đắc lực phục vụ sản xuất v&agrave; chiến đấu: chở h&agrave;ng ho&aacute; tại những nơi &ocirc; t&ocirc; kh&ocirc;ng đi được; chở người đi sơ t&aacute;n, chở thương binh đến nơi cấp cứu, chở c&aacute;c phương tiện chữa ch&aacute;y, chở đạn dược, kh&iacute; t&agrave;i ra trận địa ...X&iacute;ch l&ocirc; c&oacute; thể chở được tới h&agrave;ng trăm kg h&agrave;ng h&oacute;a. Chiếc x&iacute;ch l&ocirc; đ&aacute;ng c&oacute; vị tr&iacute; trong bảo t&agrave;ng H&agrave; Nội thời chiến. Thế rồi, từ khi Đổi mới, nhiều quan niệm được thay đổi, nhu cầu đời sống ng&agrave;y một phong ph&uacute;, người nước ngo&agrave;i đến du lịch c&agrave;ng nhiều, x&iacute;ch l&ocirc; lại c&oacute; th&ecirc;m chức năng dịch vụ thương mại phục vụ kh&aacute;ch du lịch b&ecirc;n cạnh chức năng vận chuyển h&agrave;ng h&oacute;a. Theo thống k&ecirc; của Ph&ograve;ng Cảnh s&aacute;t giao th&ocirc;ng H&agrave; Nội, to&agrave;n th&agrave;nh phố hiện c&oacute; 5 c&ocirc;ng ty hoạt động vận chuyển bằng xe x&iacute;ch l&ocirc; với 388 xe.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="" height="333" src="http://www.abay.vn/images/upload/5494692460_a09050e7d0.jpg" style="border:none;vertical-align:middle;" width="500" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hiện tại Sans-Souci l&agrave; h&atilde;ng x&iacute;ch l&ocirc; lớn nhất tại H&agrave; Nội với khoảng 160 chiếc x&iacute;ch l&ocirc; đang hoạt động, v&agrave; hoạt động rất chuy&ecirc;n nghiệp. Ri&ecirc;ng ngồi với &ocirc;ng nửa buổi chiều m&agrave; đ&atilde; c&oacute; rất nhiều c&uacute; điện thoại gọi đến cần dịch vụ của &ocirc;ng. Một l&agrave; cho tour du lịch, hai l&agrave; phục vụ đ&aacute;m cưới, c&ograve;n số &iacute;t l&agrave; cho người đi dạo phố.&nbsp;Phố H&agrave; Nội đ&atilde; quen lắm với x&iacute;ch l&ocirc; San-Souci, nhưng chỉ l&agrave; trong l&ograve;ng phố cổ. Những anh, những b&aacute;c cả l&aacute;i x&iacute;ch l&ocirc; l&uacute;c n&agrave;o cũng mỉm cười kh&ocirc;ng mệt mỏi, d&ugrave; mồ h&ocirc;i đang ướt đầm vai &aacute;o.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt="" height="350" src="http://www.abay.vn/images/upload/xe.jpg" style="border:none;vertical-align:middle;" width="490" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Gặp một đo&agrave;n du lịch Ph&aacute;p bước xuống xe tr&ecirc;n đường L&yacute; Th&aacute;i Tổ, t&ocirc;i thấy gương mặt họ s&aacute;ng ngời v&agrave; lộ vẻ h&agrave;i l&ograve;ng. &ldquo;How do you feel about this service?&rdquo; &ldquo;Very good! This man&rsquo;s so wonderful and friendly!&rdquo; Tất cả kh&aacute;ch Ph&aacute;p đều mỉm cười đồng t&igrave;nh. Kh&aacute;ch v&agrave; chủ c&ograve;n trao đổi v&agrave;i c&acirc;u th&acirc;n thiện nữa trước khi &ocirc;t&ocirc; đến đ&oacute;n kh&aacute;ch trở về.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Hệ&nbsp;thống&nbsp;đặt v&eacute; m&aacute;y bay trực tuyến&nbsp;<a href="http://www.abay.vn/abayv2/" id="link__inpage_11">www.abay.vn&nbsp;</a>lu&ocirc;n cập nhật v&agrave; cung cấp&nbsp;<a href="http://www.abay.vn/Ve-noi-dia/ve-may-bay-di-ha-noi" id="link__inpage_12">v&eacute; m&aacute;y bay đi H&agrave; Nội</a>&nbsp;v&agrave; nhiều điểm đến trong nước, quốc tế. Hệ&nbsp;thống cho ph&eacute;p kh&aacute;ch h&agrave;ng c&oacute; thể&nbsp;so s&aacute;nh gi&aacute;&nbsp;<a href="http://www.abay.vn/abayv2/" id="link__inpage_13">v&eacute; m&aacute;y bay</a>&nbsp;của hơn 100 h&atilde;ng h&agrave;ng kh&ocirc;ng v&agrave;<a href="http://www.abay.vn/Ve-noi-dia/ve-may-bay-di-ha-noi" id="link__inpage_14">book</a><a href="http://www.abay.vn/Ve-noi-dia/ve-may-bay-di-ha-noi" id="link__inpage_15">&nbsp;v&eacute; m&aacute;y bay H&agrave; Nội&nbsp;</a>một c&aacute;ch nhanh ch&oacute;ng&nbsp;qua hệ&nbsp;thống thanh to&aacute;n tiện lợi với mức gi&aacute; tốt nhất v&agrave;&nbsp;đội ngũ&nbsp;tư&nbsp;vấn, chăm s&oacute;c kh&aacute;ch h&agrave;ng phục vụ&nbsp;24/7.</p>\r\n<br />\r\n');
